package MethodA_Arguments_pckg;

public class Method_Arguments_Class {
	
	public static void add(int a1, int a2) {
		int sum= a1+a2;
		System.out.println(sum);
	}
	
	public static void sub(int a3, int a4) {
		int sum=a3-a4;
		System.out.println(sum);
	}
	
	public static void mul(int a5, int a6) {
		int sum=a5*a6;
		System.out.println(sum);
	}
	
	

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		Method_Arguments_Class.add(200, 300);
		Method_Arguments_Class.sub(300, 100);
		Method_Arguments_Class.mul(200, 100);
		
		
		
		
			
		
		

	}

}
