package pckg_04_10_2025;

public class MethodA_1 {
	
	public static void add(int a1, int a2) {
		
		int sum=a1+a2;
		
		System.out.println(sum);
	}
	
	public static void sub(int a3, int a4) {
		
		int sum=a3-a4;
		
		System.out.println(sum);
	}
	public static void div(int a5, int a6) {
		
		int sum=a5/a6;
		
		System.out.println(sum);
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		MethodA_1.add(5000, 6000);
		MethodA_1.add(500, 600);
		MethodA_1.add(400, 200);

	}

}
